mapper.select:
SELECT METER.MDS_ID AS meterId , TO_CHAR(TRUNC(sysdate,'MM'), 'yyyymmdd') as startDate , TO_CHAR(SYSDATE, 'yyyymmdd') as endDate , MAX(BILL.ACCUMULATEUSAGE) as usage , 'kWh' as usageUnit , MAX(BILL.ACCUMULATEBILL) as credit , 'GHC' as creditUnit FROM CONTRACT CONT LEFT OUTER JOIN CUSTOMER CUST ON CONT.CUSTOMER_ID = CUST.ID LEFT OUTER JOIN METER METER ON CONT.METER_ID = METER.ID LEFT OUTER JOIN BILLING_BLOCK_TARIFF BILL ON BILL.METER_ID = METER.ID WHERE 1=1
AND BILL.MDEV_ID = #{meterId}
AND BILL.YYYYMMDD BETWEEN TO_CHAR(TRUNC(SYSDATE,'MM'), 'yyyymmdd') AND TO_CHAR(SYSDATE, 'yyyymmdd') GROUP BY METER.MDS_ID , TO_CHAR(TRUNC(SYSDATE,'MM') , 'yyyymmdd'), TO_CHAR(SYSDATE, 'yyyymmdd')
